function getRandomOuterCellIndices(rows, columns) {
  // Ensure that the matrix has at least 1 row and 1 column
  if (rows < 1 || columns < 1) {
      throw new Error("Matrix must have at least 1 row and 1 column");
  }

  // Determine whether to select a cell from the outer rows or columns
  const selectFromRows = Math.random() < 0.5;

  if (selectFromRows) {
      // Select a random row index from the first or last row
      const rowIndex = Math.random() < 0.5 ? 0 : rows - 1;
      // Select a random column index
      const columnIndex = Math.floor(Math.random() * columns);

      return { i: rowIndex, j: columnIndex };
  } else {
      // Select a random column index from the first or last column
      const columnIndex = Math.random() < 0.5 ? 0 : columns - 1;
      // Select a random row index
      const rowIndex = Math.floor(Math.random() * rows);

      return { i: rowIndex, j: columnIndex };
  }
}

function shuffleArrayKeepingIndex(array, index) {
  // Check if the index is within the bounds of the array
  if (index < 0 || index >= array.length) {
      throw new Error("Index is out of bounds for the given array");
  }

  // Create a copy of the array to avoid modifying the original array
  const shuffledArray = array.slice();

  // Perform Fisher-Yates shuffle
  for (let i = shuffledArray.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));

      // Swap elements
      [shuffledArray[i], shuffledArray[j]] = [shuffledArray[j], shuffledArray[i]];
  }

  // Swap the element at the specified index back to its original position
  [shuffledArray[index], shuffledArray[array.length - 1]] = [shuffledArray[array.length - 1], shuffledArray[index]];

  return shuffledArray;
}

export function multiplyArrayElements(arr, multiplier) {
  // Check if the input is an array and multiplier is a number
  if (!Array.isArray(arr) || typeof multiplier !== 'number') {
      console.error('Invalid input. Please provide an array and a number.');
      return null;
  }

  // Multiply each element in the array by the multiplier
  const multipliedArray = arr.map(element => element * multiplier);

  return multipliedArray;
}