export function mapValues(values, targetMin, targetMax) {
  const min = Math.min(...values);
  const max = Math.max(...values);

  const scaleFactor = (targetMax - targetMin) / (max - min);

  const mappedValues = values.map(value => targetMin + (value - min) * scaleFactor);

  return mappedValues;
}


//function to transform 1D array of values to matrix format
export function arrayToMatrix(arr, n) {
    if (arr.length !== n * n) {
      throw new Error('Input array length does not match the desired matrix size');
    }
  
    const matrix = [];
    for (let i = 0; i < n; i++) {
      matrix.push(arr.slice(i * n, (i + 1) * n));
    }
  
    return matrix;
  }

