//function to find all the possible locations to put the two values for the "compare" task
export function findOuterPairsWithDistance(matrixSize, maxDistance) {
    const outerPairs = [];
  
    // Iterate through the outer rows and columns
    for (let i = 0; i < matrixSize; i++) {
      for (let j = 0; j < matrixSize; j++) {
        if (
          i === 0 || // Top row
          i === matrixSize - 1 || // Bottom row
          j === 0 || // Left column
          j === matrixSize - 1 // Right column
        ) {
          // Iterate through other outer cells
          for (let k = 0; k < matrixSize; k++) {
            for (let l = 0; l < matrixSize; l++) {
              if (
                (k === 0 || k === matrixSize - 1 || l === 0 || l === matrixSize - 1) && // Check if the other cell is also on the outer rows or columns
                (i !== k) && (j !== l) // Check if the cells are not in the same row or column
              ) {
                // Calculate the distance between the current cells
                const distance = Math.abs(i - k) + Math.abs(j - l);
  
                // Check if the distance matches the desired distance
                if (distance === maxDistance) {
                  outerPairs.push({ cell1: [i, j], cell2: [k, l] });
                }
              }
            }
          }
        }
      }
    }
  
    return outerPairs;
}

